    {
        "id": 202,
        "quote": "A day without laughter is a day wasted.",
        "author": {
            "id": 276,
            "name": "Nicolas Chamfort",
            "slug": "nicolas-chamfort",
            "image": "https://images.gr-assets.com/authors/1420504765p5/308646.jpg"
