    },
    {
        "id": 208,
        "quote": "I became insane, with long intervals of horrible sanity.",
        "author": {
            "id": 83,
            "name": "Edgar Allan Poe",
            "slug": "edgar-allan-poe",
            "image": "https://images.gr-assets.com/authors/1454522972p5/4624490.jpg"
